package printhello;

public class ArrayExamplePlaylist {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Array methods
		//First method
		
    	String[] playlist=new String[5];
		System.out.println("PLAYLISTS");
		
		playlist[0]="SONG 1";
		System.out.println(playlist[0]);
		
		playlist[1]="SONG 2";
		System.out.println(playlist[1]);
		
		playlist[2]="SONG 3";
		System.out.println(playlist[2]);
		
		playlist[3]="SONG 4";
		System.out.println(playlist[3]);
		
		playlist[4]="SONG 5";
		System.out.println(playlist[4]);
//	--------------------------------------------------------------------------------------------------------------------------	
		//Second method
		
		System.out.println("PLAYLISTS");
		String[] playlist1= {"SONG 1","SONG 2","SONG 3","SONG 4","SONG 5"};
		
		System.out.println(playlist1[0]);
		System.out.println(playlist1[1]);
		System.out.println(playlist1[2]);
		System.out.println(playlist1[3]);
		System.out.println(playlist1[4]);

	}

}
